const {
  ytdlmp3,
  ytdlmp4,
  ytsearch
} = require('.');

(async () => {
  try {
    // Buscar v√≠deos
    const resultados = await ytsearch("trap nation 2025", 3);
    console.log("Resultados:", resultados);

    // Baixar MP3
    const mp3 = await ytdlmp3("https://www.youtube.com/watch?v=dQw4w9WgXcQ", 192);
    console.log("MP3:", mp3);

    // Baixar MP4
    const mp4 = await ytdlmp4("https://www.youtube.com/watch?v=dQw4w9WgXcQ", 720);
    console.log("MP4:", mp4);

  } catch (err) {
    console.error("Erro:", err.message);
  }
})();